        string memory symbol_,
        uint8 decimals_,
        uint256 feeRate_,
        uint256 maxFee_,
        address feeCollector_
    ) {
        require(
            feeRate_ <= maxFee_,
            "ERC20WithFees: fee rate cannot be greater than 10%"
        );
        _name = name_;
        _symbol = symbol_;
        _decimals = decimals_;
        feeRate = feeRate_;
        _feeCollector = feeCollector_;
        lastFeeChange = block.timestamp;
        feePrecision = 30 days * 10 ** decimals_;
        maxFee = maxFee_;
